import React, {useEffect, useState} from 'react';
import {fetchFromAPI} from "../../fetchFromApi";
import {useParams} from "react-router-dom";
const News = () => {
    const {id} = useParams()
    const [card,setCard] = useState([])

    useEffect(() => {
         fetchFromAPI()
             .then(data => {
                 const sortedData = data.articles.sort((a,b) => {
                     return new Date(b.publishedAt) - new Date(a.publishedAt)
                 })
                 setCard(sortedData);
             } )
    },[])
    if (card === null) {
        return <div>Loading...</div>;
    }

    if (card.length === 0) {
        return <div>No data available</div>;
    }

    return (
        <div className={'card'}>
            <div className="container">
               <div className="card__row">
                   <div className="card__row__side">
                       <img width={'100%'} height={'100%'} src={card[id].imgToUrl} alt="image"/>
                   </div>
                   <div className="card__row__side">
                       <h2>{card[id].author}</h2>
                       <br/>
                       <p>{card[id].content}</p>
                       <br/>
                       <p>Опубликовано: {card[id].publishedAt}</p>
                       <br/>
                       <p><a href={card[id].url}>ссылка на сайт-источник</a></p>
                   </div>
               </div>
            </div>
        </div>
    );
};

export default News;
