import React, {useState, useEffect} from 'react';
import {fetchFromAPI} from "../../fetchFromApi";
import {useNavigate} from "react-router-dom";

const Home = () => {

   const [news,setNews] = useState([])
    const [search,setSearch] = useState('')
    useEffect(() => {
         fetchFromAPI(search && `articles?`)
             .then(data =>  setNews(data.articles))
    },[search])

    const sortedData = news.sort((a,b) => {
        return new Date(b.publishedAt) - new Date(a.publishedAt)
    })
    const nav = useNavigate()
    return (
        <>
            <header className={'header'}>
                <div className="container">
                    <div className="header__row">
                        <h1>Главная страница</h1>
                        <input value={search} onChange={(e) => setSearch(e.target.value)} className={'header__input'} placeholder={'поиск'} type="text"/>
                    </div>
                </div>
            </header>
            <main className="main">
                <section className="news">
                    <div className="container">
                        <div className="news__row">
                            {
                                sortedData.slice(0,15).map((item,index) => (
                                    <div key={index} className={'news__row__card'}>
                                        <img width={'100%'} height={180} src={item.urlToImage} alt=""/>
                                        <div className="news__row__card__cont">
                                            <h2>{item.author}</h2>
                                            <br/>
                                            <p>{item.content}</p>
                                            <br/>
                                            <p>Опубликовано: {item.publishedAt}</p>
                                            <br/>
                                            <p>Язык:  'Русский'</p>
                                            <br/>
                                            <p><a href={item.url}>ссылка на сайт-источник</a></p>
                                            <br/>
                                            <p>Рейтинг:</p>
                                            <button onClick={() => nav(`news/${item.source.name}`)} className="news__row__card__btn">
                                                Подробнее
                                            </button>
                                        </div>
                                    </div>
                                ))
                            }
                        </div>
                    </div>
                </section>
            </main>
        </>
    );
};

export default Home;
