import React, {useState, useEffect} from 'react';
import {fetchFromAPI} from "../../fetchFromApi";

const Home = () => {

   const [news,setNews] = useState([])
    useEffect(() => {
         fetchFromAPI()
             .then(data =>  setNews(data.news.news))
    },[])


    return (
        <>
            <header className={'header'}>
                <div className="container">
                    <div className="header__row">
                        <h1>Главная страница</h1>
                        <input className={'header__input'} placeholder={'поиск'} type="text"/>
                    </div>
                </div>
            </header>
            <main className="main">
                <section className="news">
                    <div className="container">
                        <div className="news__row">
                            {
                                news.map(item => (
                                    <div key={item.props.link} className={'news__row__card'}>
                                        <img width={'100%'} height={180} src={item.image} alt=""/>
                                        <div className="news__row__card__cont">
                                            <h2>{item.source}</h2>
                                            <p>{item.title}</p>
                                            <div className="news__row__card__info">
                                                <p>Опубликовано: {item.created_at}</p>
                                                <p>Язык: {item.language}</p>
                                            </div>
                                            <div className="news__row__card__info">
                                                <p>Источник: {item.link}</p>
                                                <p>Рейтинг: {item._score}</p>
                                            </div>
                                        </div>

                                    </div>
                                ))
                            }
                        </div>
                    </div>
                </section>
            </main>
        </>
    );
};

export default Home;
