import React, { useState } from 'react';
import axios from 'axios';

const SearchNews = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);

    const handleSearch = async () => {
        if (searchQuery.trim() !== '') {
            const encodedQuery = encodeURIComponent(searchQuery);
            const searchUrl = `https://google-news-api1.p.rapidapi.com/search?q=${encodedQuery}`;
            const apiKey = '73ea4e6774mshe9f64e22e1cbb20p1d33c2jsn792786d22482'
            const requestUrl = `${searchUrl}&rapidapi-key=${apiKey}`;

            try {
                const response = await axios.get(requestUrl);
                const news = response.data.news.news;
                setSearchResults(news);
            } catch (error) {
                console.error('Ошибка при выполнении запроса:', error);
            }
        }
    };

    const displayResults = () => {
        if (searchResults.length > 0) {
            return searchResults.map(result => (
                <div key={result.title}>
                    <h2>{result.title}</h2>
                    <p>{result.description}</p>
                    <a href={result.link} target="_blank" rel="noopener noreferrer">Читать далее</a>
                </div>
            ));
        } else {
            return <p>Нет результатов поиска</p>;
        }
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Введите поисковый запрос"
                value={searchQuery}
                onChange={event => setSearchQuery(event.target.value)}
            />
            <button onClick={handleSearch}>Поиск</button>
            <div>{displayResults()}</div>
        </div>
    );
};

export default SearchNews;
