import React, {useEffect, useState} from 'react';
import {fetchFromAPI} from "../../fetchFromApi";
import {useParams} from "react-router-dom";
const News = () => {
    const {id} = useParams()
    const [card,setCard] = useState(null)

    useEffect(() => {
         fetchFromAPI()
             .then(data => {
                 const sortedData = data.articles.sort((a,b) => {
                     return new Date(b.publishedAt) - new Date(a.publishedAt)

                 })
                 setCard(sortedData);
             } )
    },[])


    return (
        <div className={'card'}>
            <div className="container">
                <div key={card[id]} className={'news__row__card'}>
                    <img width={'100%'} height={180} src={sortedData[id].urlToImage} alt=""/>
                    <div className="news__row__card__cont">
                        <h2>{sortedData[id].author}</h2>
                        <br/>
                        <p>{sortedData[id].content}</p>
                        <br/>
                        <p>Опубликовано: {sortedData[id].publishedAt}</p>
                        <br/>
                        <p>Язык:  'Русский'</p>
                        <br/>
                        <p><a href={sortedData[id].url}>ссылка на сайт-источник</a></p>
                        <br/>
                        <p>Рейтинг:</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default News;
